import React from "react";
import './sample.css';
import web from '../mac.jpg'
import { useNavigate } from "react-router";
import { useState } from "react";
import { useEffect } from "react";
import axios from "axios";






const contacts = [
  {
    id: 1,
    name: "Nora",
    imgURL:
      "https://static.toiimg.com/photo/msid-87698129/87698129.jpg?62448",
    p: "+123 456 789",
    email: "Vote",
    phone:'cancel'
    
  },
 
  {
    id: 3,
    name: "yaswith",
    imgURL:
      "https://i.pinimg.com/originals/e3/94/47/e39447de921955826b1e498ccf9a39af.png",
    p: "+918 372 574",
    email: "Vote",
    phone:'cancel'
  },

  {
    id: 1,
    name: "Nora",
    imgURL:
      "https://static.toiimg.com/photo/msid-87698129/87698129.jpg?62448",
    p: "+123 456 789",
    email: "Vote",
    phone:'cancel'
    
  }


];


function Vt() {
  return (
    <div>
      <h1 style={{textAlign:'center'}} className="heading">Candidates</h1>
      <h3 style={{color:'black'}} > Vote for only one candidate</h3>
      <div>
      <Cvote /> </div>
    </div>
  );
}



function Cvote(props) {

  const [cand, setcand] = useState([]);

  useEffect(()=>{
    axios.get("http://localhost:8000/reg/cand").then((res) => {
    setcand(res.data);
      console.log(res.data);
      
  });
},[]);


  return (
<div>

</div>
 
  );
}

function Avatar(props) {
  return <img className="circle-img" src={props.img} alt="avatar_img" />;
}

function Detail(props) {
  return  <button type="button"  class="btn btn-outline-dark off">{props.detailInfo}</button>;

}

function Card(props) {
  let l=useNavigate()

  const [listofposition, setlistofposition] = useState( [] );

  useEffect(()=>{
    axios.get("http://localhost:8000/reg/position").then((res) => {
      setlistofposition(res.data);
      console.log(res.data);
      
  });
},[]);


  return (
  <div>
    {listofposition.map((value, key)=>{
      return <div style={{paddingRight:'10px' }} class="w3-col l4 m6 w3-margin-bottom">
      <div class="w3-card">
          <img src={web} alt="John" style={{width:"100%"}} />
          <div class="w3-container">
              <h4>{value.description}</h4>
              <p class="w3-opacity">////</p>
              <p>{value.description}</p>
              <p ><button onClick={()=>{
                l('/Vote')
              }  } class="w3-button w3-light-grey w3-block"> View</button></p>
          </div>
      </div>
  </div>
    })}
  </div>
  );
}







function Ei() {

  return (
    <div >

      <Card />
      

    </div>
  );
}

export default Ei
export {Vt}
