import React from "react";
import './sample.css';
import web from '../mac.jpg'
import { useNavigate } from "react-router";
import { useState } from "react";
import { useEffect } from "react";




const [ListofPosition, setListofPosition] = useState('');

const contacts = [
  {
    id: 1,
    name: "Nora",
    imgURL:
      "https://static.toiimg.com/photo/msid-87698129/87698129.jpg?62448",
    p: "+123 456 789",
    email: "Vote",
    phone:'cancel'
    
  },
 
  {
    id: 3,
    name: "yaswith",
    imgURL:
      "https://i.pinimg.com/originals/e3/94/47/e39447de921955826b1e498ccf9a39af.png",
    p: "+918 372 574",
    email: "Vote",
    phone:'cancel'
  },

  {
    id: 1,
    name: "Nora",
    imgURL:
      "https://static.toiimg.com/photo/msid-87698129/87698129.jpg?62448",
    p: "+123 456 789",
    email: "Vote",
    phone:'cancel'
    
  }


];

function CreateCard(contact) {
  return (
    <Cardvote
      key={contact.id}
      name={contact.name}
      img={contact.imgURL}
      tel={contact.phone}
      email={contact.email}
    />
  );
}
function Vt() {
  return (
    <div>
      <h1 style={{textAlign:'center'}} className="heading">Candidates</h1>
      <h3 style={{color:'black'}} > Vote for only one candidate</h3>
      <div>
      {contacts.map(CreateCard)} </div>
    </div>
  );
}



function Cardvote(props) {
  return (
    <div style={{paddingRight:'10px;'}} className="card w3-col l4 m6  w3-margin-bottom">
      <div className="top">
        <h2 className="name">{props.name}</h2>
        <Avatar img={props.img} />
      </div>
      <div className="bottom">
      <Detail detailInfo={props.email} />
    
      </div>
    </div>
  );
}

function Avatar(props) {
  return <img className="circle-img" src={props.img} alt="avatar_img" />;
}

function Detail(props) {
  return  <button type="button"  class="btn btn-outline-dark off">{props.detailInfo}</button>;

}

function Card(props) {

  useEffect(()=>{
    axios.get("http://localhost:8000/reg/position").then((res) => {
      setListofPosition(res.data);
  });
},[]);

  let l=useNavigate()
  return (
    <div style={{paddingRight:'10px' }} class="w3-col l4 m6 w3-margin-bottom">
    <div class="w3-card">
        <img src={web} alt="John" style={{width:"100%"}} />
        <div class="w3-container">
            <h4>{props.name}</h4>
            <p class="w3-opacity">////</p>
            <p>{props.p}</p>
            <p ><button onClick={()=>{
              l('/Vote')
            }} class="w3-button w3-light-grey w3-block"> View</button></p>
        </div>
    </div>
</div>
  );
}




function createCard(contact) {
  return (
    <div>
    <Card
    
      name={contact.name}
      p={contact.p}
      
    />
    </div>
  );
}


function Ei() {
  return (
    <div >

      {contacts.map(createCard)}
      

    </div>
  );
}

export default Ei
export {Vt}
