import React from "react";
import axios from "axios";
import { useEffect } from "react";
import { useState } from "react";
import Main from "./main";
import './sample.css'
import { useNavigate } from "react-router";
import web from '../mac.jpg'
import { Avatar } from "./sample";

function Admin(props){


  return(
    <div>
       <Main ad='Addcontest' page='Admin' card={<Adminmain />}  />
    </div>
  );
}

function AdminCand(props){


  return(
    <div>
       <Main ad='Addcontest' page='Result' card={<Acandidatecard />}  />
    </div>
  );
}



function Adminmain(props){

  
  

  const [listofpositiona, setlistofpositiona] = useState( [] );
  

  useEffect(()=>{
    axios.get("http://localhost:8000/reg/position").then((res) => {
      setlistofpositiona(res.data);
      console.log(res.data);
      
  });
},[]);
  
    return(
      <div>
         <div>
    {listofpositiona.map((value, key)=>{
      return <div style={{paddingRight:'10px' }} class="w3-col l4 m6 w3-margin-bottom">
      <div class="w3-card">
          <img src={web} alt="John" style={{width:"100%"}} />
          <div class="w3-container">
              <h4>{value.description}</h4>
              <p class="w3-opacity"></p>
              <p ><button onClick={()=>{
                l(`/Avote/${value.id}`);
      
                  localStorage.setItem('des', value.description)
             
               } } class="w3-button w3-light-grey w3-block"> View</button></p>
          </div>
      </div>
  </div>
    })}
  </div>
  );



  
      </div>
    );
  
  
  }


  function Acandidatecard(props){

    const [acand, setacand] = useState([]);
   
  
  
    let {id} =useParams();

    useEffect(()=>{
      axios.get(`http://localhost:8000/reg/cand/${id}`).then((res) => {
      setacand(res.data);
      
        console.log(res.data);
        
    });
   
  
  
  },[]);

    return(
      <div>
         <div>
      <h1 style={{textAlign:'center'}} className="heading">Candidates for {localStorage.getItem('des')}</h1>
      <h3 style={{color:'black'}} > Vote for only one  candidate</h3>
      <div>
      <div>
    {acand.map((value, key)=>{
      
      return  <div style={{paddingRight:'10px;'}} className="card w3-col l4 m6  w3-margin-bottom">
      <div className="top">
        <h2  className="name">{value.name}</h2>
        <Avatar img={value.photo} />
      </div>
      <div className="bottom">
    <a  class="btn btn-outline-dark off">Vote </a>
      
      </div>
    </div>
    })}
  </div>
  
     </div>
  
    </div>
      </div>
    );
  }

  export default Admin;
  export {Adminmain}
export {Acandidatecard}
