import {React,useRef} from "react";
import axios from "axios";
import { useEffect } from "react";
import { useState } from "react";
import Main from "./main";

import { useNavigate } from "react-router";
import web from '../mac.jpg'
import { Avatar } from "./sample";
import { useParams } from "react-router";
import './mac.jpg'
import Time from "./Time";

function Admin(props){


  return(
    <div>
       <Main ad='Addcontest'  home='/pro' votehere='/pro' page='Admin' card={<Adminmain />}  />
    </div>
  );
}

function Admincand(props){


  return(
    <div>
       <Main ad='Addcontest'  home='/pro' votehere='/pro' page='Result' admin={<Canform />} card={<Acandidatecard />}  />
    </div>
  );
}



function Adminmain(props){

 let l=useNavigate();
 let pass="/pro";
  

  const [listofpositiona, setlistofpositiona] = useState( [] );
  

  useEffect(()=>{
    axios.get("http://localhost:8000/reg/position").then((res) => {
      setlistofpositiona(res.data);
      console.log(res.data);
  });
},[]);
    return(
      <div>
          {/* <div>
    {listofpositiona.map((value, key)=>{
      return <div style={{paddingRight:'10px' }} class="w3-col l4 m6 w3-margin-bottom">
      <div class="w3-card">
          <img src={web} alt="John" style={{width:"100%"}} />
          <div class="w3-container">
              <h4>{value.description} </h4>
              <Time Datee={value.Date} month={value.Month} nav={pass} year={value.Year} iid={value.id} hours={value.Hours} minutes={value.Minutes} seconds={value.Seconds} />
              <p class="w3-opacity"></p>
              <p ><button onClick={()=>{
                l(`/Avote/${value.id}`)
                  localStorage.setItem('aposid',value.id);
                  localStorage.setItem('des', value.description)
               } } class="w3-button w3-light-grey w3-block"> View</button></p>
               <p><button onClick={()=>{
                      axios.get(`http://localhost:8000/reg/posdelete/${value.id}`).then((res) => {
                        window.location.reload();
                      })}
               } class='w3-button w3-light-grey w3-block'>Delete</button></p>
          </div>
      </div>
  </div>
    })}
  </div> */}
  

 */
   <div>
    {listofpositiona.map((value, key)=>{
      
      return <div style={{color:'white',background: 'rgb(0,1,36)',
      background: 'linear-gradient(90deg, rgba(0,1,36,0.9026961126247374) 6%, rgba(9,9,121,1) 33%, rgba(0,212,255,1) 83%)'}} class="card w-75">
         
  <div class="card-body">
  <div style={{display:"flex",justifyContent:'space-between'}}>
  <h4 style={{marginBottom:'12px'}}>{value.id}.{value.description}</h4>
   <div  style={{color:'white',display:'flex',justifyContent:'right'}}> 
   <div><button
   onClick={()=>{
    l(`/Avote/${value.id}`)
      localStorage.setItem('aposid',value.id);
      localStorage.setItem('des', value.description)
   } }
     class="btn btn-primary w-50">View</button>
     </div>
     <div>
     <div  style={{color:'white'}}> <button
   onClick={()=>{
    axios.get(`http://localhost:8000/reg/posdelete/${value.id}`).then((res) => {
      window.location.reload();
    })}
} 
     class="btn btn-primary w-50">Delete</button></div>
      </div></div>
    
     </div>
            <div><Time Datee={value.Date} month={value.Month} id={value.id} nav={pass} year={value.Year} hours={value.Hours} minutes={value.Minutes} seconds={value.Seconds} /></div>
  </div>
</div>
 
     })}
 </div>

  
      </div>
    );
  
  
  }


  function Acandidatecard(props){

    const [acand, setacand] = useState([]);
    const [cname, setcname] = useState('');
    const [cimg, setcimg] = useState('');
  
  let m=useNavigate();
  
  const [listofposition, setlistofposition] = useState( [] );
  
    let {id} =useParams();

    useEffect(()=>{
      axios.get(`http://localhost:8000/reg/cand/${id}`).then((res) => {
      setacand(res.data);
      
        console.log(res.data);
        
    });

      axios.post(`http://localhost:8000/reg/posone/${id}`).then((res) => {
