import React from "react";
import './sample.css';
import web from '../mac.jpg'
import { useNavigate } from "react-router";
import { useState } from "react";
import { useEffect } from "react";
import axios from "axios";
import { useParams } from 'react-router-dom'













function Vt() {
  const [cand, setcand] = useState([]);
  const [cid, setcid] = useState('');
  const [uid, setuid] = useState('');
  let {id} =useParams();



  const Voted = (req, res) => {
    const data ={cid:value.id, uid: localStorage.getItem('usid') };
    useEffect(()=>{
      axios.post(`http://localhost:8000/reg/Votes/${id}`, data).then((res) => {
        

        console.log(res.data);
        
    });
  },[]);
};



  useEffect(()=>{
    axios.get(`http://localhost:8000/reg/cand/${id}`).then((res) => {
    setcand(res.data);
    
      console.log(res.data);
      
  });
},[]);

  return (
    <div>
      <h1 style={{textAlign:'center'}} className="heading">Candidates</h1>
      <h3 style={{color:'black'}} > Vote for only  candidate</h3>
      <div>
      <div>
    {cand.map((value, key)=>{
      return  <div style={{paddingRight:'10px;'}} className="card w3-col l4 m6  w3-margin-bottom">
      <div className="top">
        <h2 className="name">{value.name}</h2>
        <Avatar img='https://static.toiimg.com/photo/msid-87698129/87698129.jpg?62448' />
      </div>
      <div className="bottom">
      <a  onClick={Voted} class="btn btn-outline-dark off">Vote</a>
      </div>
    </div>
    })}
  </div>
     </div>
    </div>
  );
}




function Avatar(props) {
  return <img className="circle-img" src={props.img} alt="avatar_img" />;
}


function Card(props) {
  let l=useNavigate()

  const [listofposition, setlistofposition] = useState( [] );

  useEffect(()=>{
    axios.get("http://localhost:8000/reg/position").then((res) => {
      setlistofposition(res.data);
      console.log(res.data);
      
  });
},[]);


  return (
  <div>
    {listofposition.map((value, key)=>{
      return <div style={{paddingRight:'10px' }} class="w3-col l4 m6 w3-margin-bottom">
      <div class="w3-card">
          <img src={web} alt="John" style={{width:"100%"}} />
          <div class="w3-container">
              <h4>{value.description}</h4>
              <p class="w3-opacity">////</p>
              <p>{value.description}</p>
              <p ><button onClick={()=>{
                l(`/Vote/${value.id}`)
                localStorage.setItem('pid', value.id )
              }  } class="w3-button w3-light-grey w3-block"> View</button></p>
          </div>
      </div>
  </div>
    })}
  </div>
  );
}







function Ei() {

  return (
    <div >

      <Card />
      

    </div>
  );
}

export default Ei
export {Vt}
