import React from "react";
import { Header } from "./homepage";
import { useState } from "react";
import './login.css';
import { useNavigate } from "react-router";
import axios from "axios";
 


function Loginpage(props){

    let s = useNavigate();
  function signupr(){
    s('/sign')
  }
  function homer(){
    s('/')
}
    return(
        <div>
<Header login='Sign Up' h={homer} home='Home' loginfont='fa fa-usd' navi={signupr} />
<Auth login="card-front" signup="card-back" header1="Log In" header2="Sign Up" />
        </div>
    );
}



function Register(props){


    let l = useNavigate();
    function loginr(){
      l('/login')
    }
    function homer(){
        l('/')
    }


    return(
        <div>
          <Header login='login' h={homer} home='Home' loginfont='fa fa-usd' navi={loginr}  />
<Auth signup="card-front" login="card-back" header2="Log In" header1="Sign Up" />
    
        </div>
    );
}

function Auth(props){
let	l=useNavigate();
let m=useNavigate();
	const [username, setusername] = useState("");
    const [password, setpassword] = useState("");
    const [roll, setroll] = useState("");
    const [email, setemail] = useState("");
	const [lusername, setlusername] = useState("");
    const [lpassword, setlpassword] = useState("");
	const [loginstatus,setloginstatus] =useState(false);


	const sub = (req, res) => {
        const data = { username: username, password: password, roll:roll, email: email };
        axios.post("http://localhost:8000/reg/signup", data).then((res) => {
			if(res.data.message==="SUCCESS"){
			
				m('/');
				
			}
			else{
			
			}


        });
    };
	const lsub = (req, res) => {
        const data = { username: lusername, password: lpassword,  };
        axios.post("http://localhost:8000/reg/login", data).then((res) => {
			if(!res.data.auth){
				console.log(res);
				setloginstatus(false);
			  }
			  else{
				  localStorage.setItem("token", res.data.token);
				  console.log(res);
				  setloginstatus(true);
				  
				
				
				}
				
				  
			
			  
			  });
		  };

		
		  const AuthL = (req, res) => {
			axios.get("http://localhost:8000/reg/isUserAuth", {
				headers:{"x-access-token": localStorage.getItem("token") }, }).then((res)=>{
									if(res.data.m==='ok'){
										l('/main');
										console.log(res);
									}
									else{}
				});
			};
		
	

	






    return(
        <div>
 
	<div class="section">
		<div class="container">
			<div class="row full-height justify-content-center">
				<div class="col-12 text-center align-self-center py-5">
					<div class="section pb-5 pt-5 pt-sm-2 text-center">
						<h6 class="mb-0 pb-3"><span>{props.header1}</span><span>{props.header2}</span></h6>
			          	<input class="checkbox" type="checkbox" id="reg-log" name="reg-log"/>
			          	<label for="reg-log"></label>
						<div class="card-3d-wrap mx-auto">
							<div class="card-3d-wrapper">
								<div class={props.login}>
									<div class="center-wrap">
										<div class="section text-center">
                                            <form >
											<h4 class="mb-4 pb-3">Log In</h4>
											<div class="form-group">
												<input onChange={(event)=>{setlusername(event.target.value)}} type="email" name="logemail" class="form-style" placeholder="Your Email" id="logemail" autocomplete="off" />
												<i class="input-icon uil uil-at"></i>
											</div>	
											<div class="form-group mt-2">
												<input onChange={(event)=>{setlpassword(event.target.value)}} type="password" name="logpass" class="form-style" placeholder="Your Password" id="logpass" autocomplete="off" />
												<i class="input-icon uil uil-lock-alt"></i>
											</div>
											<a onClick={lsub} class="btn mt-4">submit</a>
											
                            				<p class="mb-0 mt-4 text-center"><a href="#0" class="link">Forgot your password?</a></p>
                                            </form>
				      					</div>
			      					</div>
			      				</div>
								<div class={props.signup}>
									<div class="center-wrap">
										<div class="section text-center">
											<form >
											<h4 class="mb-4 pb-3">Sign Up</h4>
											<div class="form-group">
												<input onChange={(event)=>{setusername(event.target.value)}} type="text" name="logname" class="form-style" placeholder="Your Full Name" id="logname" autocomplete="off" />
												<i class="input-icon uil uil-user"></i>
											</div>	
											<div class="form-group mt-2">
												<input onChange={(event)=>{setemail(event.target.value)}} type="email" name="logemail" class="form-style" placeholder="Your Email" id="logemail" autocomplete="off" />
												<i class="input-icon uil uil-at"></i>
											</div>	
											<div class="form-group mt-2">
												<input onChange={(event)=>{setpassword(event.target.value)}} type="password" name="logpass" class="form-style" placeholder="Your Password" id="logpass" autocomplete="off" />
												<i class="input-icon uil uil-lock-alt"></i>
											</div>
											<div class="form-group mt-2">
												<input onChange={(event)=>{setroll(event.target.value)}} type="text" name="logRoll" class="form-style" placeholder="Your Roll No" id="logpass" autocomplete="off" />
												<i class="input-icon uil uil-lock-alt"></i>
											</div>
											<a onClick={sub} class="btn mt-4">submit</a>
											</form>
				      					</div>
			      					</div>
			      				</div>
			      			</div>
			      		</div>
			      	</div>
		      	</div>
	      	</div>
	    </div>
	</div>
        </div>
    );
}
export default Loginpage;
export {Register}
