import "./style.css";
import '../node_modules/bootstrap/dist/css/bootstrap.min.css';
import React from 'react';
import {
    BrowserRouter as Router,Routes,Route
  } from "react-router-dom";

  import { useState } from "react";
import Homepage from "./components/homepage";
import Loginpage from "./components/loginpage";
import { Register } from "./components/loginpage";
import Main from "./components/main";
import axios from "axios";
import Sample from "./components/sample";
import { Nav } from "./components/sample";


export default function App() {

   const [aut, setaut] = useState(false)

   axios.get("http://localhost:8000/reg/isUserAuth", {
      headers:{"x-access-token": localStorage.getItem("token") }, }).then((res)=>{
                     if(res.data.m==='ok'){
                        setaut(localStorage.getItem('auth'))
                     }
                     else{}
      });
    return(
     <Router>
        <Routes>
           <Route path="/" element={<Homepage />} ></Route>
           <Route path='/login' element={<Loginpage />} ></Route>
           <Route path='/sign' element={<Register />}></Route>
           <Route path='/sample' element={<Nav />}></Route>
          {aut &&  (<Route path="/main" element={<Main />}></Route>)  }      
        </Routes>
     </Router>
    ); }
