import { React, useRef } from "react";
import axios from "axios";
import { useEffect } from "react";
import { useState } from "react";
import Main from "./main";

import { useNavigate } from "react-router";
import web from "../mac.jpg";
import { Avatar } from "./sample";
import { useParams } from "react-router";
import "./mac.jpg";
import Time from "./Time";

function Admin(props) {
  return (
    <div>
      <Main
        ad="Addcontest"
        home="/pro"
        votehere="/pro"
        page="Admin"
        card={<Adminmain />}
      />
    </div>
  );
}

function Admincand(props) {
  return (
    <div>
      <Main
        ad="Addcontest"
        home="/pro"
        votehere="/pro"
        page="Result"
        // admin={<Canform />}
        card={<Acandidatecard />}
      />
    </div>
  );
}
function AdmincandH(props) {
  return (
    <div>
      <Main
        ad="Addcontest"
        home="/pro"
        votehere="/pro"
        page="Result"
        admin={<Canform />}
        card={<AcandidatecardH />}
      />
    </div>
  );
}

function Adminmain(props) {
  let l = useNavigate();
  let pass = "/pro";

  const [listofpositiona, setlistofpositiona] = useState([]);

  useEffect(() => {
    axios.get("http://localhost:8000/reg/positionad").then((res) => {
      setlistofpositiona(res.data);
      console.log(res.data);
    });
  }, []);
  return (
    <div>
      {/* <div>
    {listofpositiona.map((value, key)=>{
      return <div style={{paddingRight:'10px' }} class="w3-col l4 m6 w3-margin-bottom">
      <div class="w3-card">
          <img src={web} alt="John" style={{width:"100%"}} />
          <div class="w3-container">
              <h4>{value.description} </h4>
              <Time Datee={value.Date} month={value.Month} nav={pass} year={value.Year} iid={value.id} hours={value.Hours} minutes={value.Minutes} seconds={value.Seconds} />
              <p class="w3-opacity"></p>
              <p ><button onClick={()=>{
                l(`/Avote/${value.id}`)
                  localStorage.setItem('aposid',value.id);
                  localStorage.setItem('des', value.description)
               } } class="w3-button w3-light-grey w3-block"> View</button></p>
               <p><button onClick={()=>{
                      axios.get(`http://localhost:8000/reg/posdelete/${value.id}`).then((res) => {
                        window.location.reload();
                      })}
               } class='w3-button w3-light-grey w3-block'>Delete</button></p>
          </div>
      </div>
  </div>
    })}
  </div> */}
      */
      <div>
        {listofpositiona.map((value, key) => {
          return (
            <div
              style={{
                color: "white",
                background: "rgb(0,1,36)",
                background:
                  "linear-gradient(90deg, rgba(0,1,36,0.9026961126247374) 6%, rgba(9,9,121,1) 33%, rgba(0,212,255,1) 83%)",
              }}
              class="card w-75"
            >
              <div class="card-body">
                <div
                  style={{ display: "flex", justifyContent: "space-between" }}
                >
                  <h4 style={{ marginBottom: "12px" }}>
                    {value.id}.{value.description}
                  </h4>
                  <div
                    style={{
                      color: "white",
                      display: "flex",
                      justifyContent: "right",
                    }}
                  >
                    <div>
                      <button
                        onClick={() => {
                          l(`/Avote/${value.id}`);
                          localStorage.setItem("aposid", value.id);
                          localStorage.setItem("des", value.description);
                        }}
                        class="btn btn-primary w-50"
                      >
                        View
                      </button>
                    </div>
                    <div>
                      <div style={{ color: "white" }}>
                        <button
                          onClick={() => {
                            axios
                              .get(`http://localhost:8000/reg/pd/${value.id}`)
                              .then((res) => {
                                window.location.reload();
                              });
                          }}
                          class="btn btn-primary w-50"
                        >
                          Delete
                        </button>
                      </div>
                    </div>
                  </div>
                </div>
                <div>
                  <Time
                    Datee={value.Date}
                    month={value.Month}
                    id={value.id}
                    nav={pass}
                    year={value.Year}
                    hours={value.Hours}
                    minutes={value.Minutes}
                    seconds={value.Seconds}
                  />
                </div>
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
}

function Acandidatecard(props) {
  const [acand, setacand] = useState([]);
  const [cname, setcname] = useState("");
  const [cimg, setcimg] = useState("");
  const [list, setlist] = useState([]);
  const [err, seterr] = useState();
  const [count, setcount] = useState("");
  const [raw, setraw] = useState([]);
  const [rescount, setrescount] = useState("");
  const [first, setfirst] = useState(second)
  let m = useNavigate();

  const [listofposition, setlistofposition] = useState([]);

  let { id } = useParams();

  useEffect(() => {
    axios.get(`http://localhost:8000/reg/cand/${id}`).then((res) => {
      setacand(res.data);

      console.log(res.data);
    });

    axios.post(`http://localhost:8000/reg/posone/${id}`).then((res) => {
      setlistofposition(res.data);
      console.log(res.data);
    });

    axios.get(`http://localhost:8000/reg/Result/${id}`).then((res) => {
      setraw(res.data.row);
      setlist(res.data.count);
      if (res.data.counte != 0) {
        if (list.length > 1) {
          if (res.data.count[0].count === res.data.count[1].count) {
            var t = res.data.count[0].count;
            var i = 0;
            while (res.data.count[i].count === t) {
              i++;
            }
            setcount(i);
          } else {
            setcount(1);
          }
        } else {
          setcount(1);
        }
      } else {
        setcount(0);
      }
    });
  }, []);

  let pass = "/pro";

  return (
    <div>
      <div>
        <div>
          <h1 style={{ textAlign: "center" }} className="heading">
            Candidates for {localStorage.getItem("des")}
          </h1>
          {listofposition.map((value, key) => {
            return (
              <div style={{ color: "black" }}>
                {" "}
                <Time
                  Datee={value.Date}
                  month={value.Month}
                  nav={pass}
                  year={value.Year}
                  iid={value.id}
                  hours={value.Hours}
                  minutes={value.Minutes}
                  seconds={value.Seconds}
                />
              </div>
            );
          })}
        </div>

        <div style={{ paddingRight: "10px;" }} class="container">
          <div class="row">
            {raw.slice(0, count).map((value, key) => {
              return (
                <div class="col-sm-12 col-md-4">
                  <div style={{ textAlign: "center", color: "black" }}>
                    {value.CandidateT.name}
                  </div>
                  <div style={{ textAlign: "center" }}>
                    <img
                      style={{
                        width: "150px",
                        height: "150px",
                        borderRadius: "50%",
                      }}
                      src="data:image/jpeg;base64,"
                      alt="Avatar"
                      class="avatar"
                    ></img>
                  </div>
                  <div style={{ textAlign: "center" }}>
                    {/* <a
                      onClick={() => {
                        m(`/Candres/}`);
                      }}
                      class="btn btn-outline-dark off"
                    >
                      View
                    </a> */}
                 <a
                        class="btn btn-outline-dark off"
                        onClick={() => {
                          axios
                            .get(`http://localhost:8000/reg/count/${value.CandidateT.id}`)
                            .then((res) => {
                              setrescount(res.data.count);
                              setrescandi(value.id);
                            });
                        }}
                      >
                        view
                      </a>
                  </div>
                </div>
              );
            })}
          </div>
        </div>

        {/* <div>
    {acand.map((value, key)=>{
      return  <div>
      <div style={{paddingRight:'10px;'}} className="card w3-col l4 m6  w3-margin-bottom">
          <div className="top">
             <h2  className="name">{value.name}</h2>
               <Avatar img={value.photo} />
          </div>
      <div className="bottom">
         <a onClick={()=>{
          m(`/Candres/${value.id}`);
            }} 
          class="btn btn-outline-dark off">View 
          </a> 
          <a onClick={()=>{
              axios.post(`http://localhost:8000/reg/candelete/${value.id}`).then((res) => {
                window.location.reload(); 
                }); } }
                 className="btn btn-outline-dark off" >Remove
          </a>
      </div>
    </div>
    </div>
    })}
  </div>
   */}
      </div>
    </div>
  );
}

function Canform(props) {
  const [cname, setcname] = useState("");
  const [file, setfile] = useState("");
  const [fileName, setfileName] = useState("");

  let { id } = useParams();

  const saveFile = (e) => {
    setfile(e.target.files[0]);
    setfileName(e.target.files[0].name);
  };

  const csub = (req, res) => {
    const formData = new FormData();

    formData.append("file", file);
    formData.append("fileName", fileName);

    const data = {
      name: cname,
      photo: fileName,
      description: localStorage.getItem("des"),
      PositionTId: id,
    };
    axios.post("http://localhost:8000/reg/candidateenter", data).then((res) => {
      if (res.data.message === "SUCCESS") {
        window.location.reload();
      } else {
      }
    });
  };

  return (
    <div>
      <div class="section">
        <div class="full-container">
          <div class="row full-height justify-content-center">
            <div class="col-12 text-center align-self-center py-5">
              <div class="section pb-5 pt-5 pt-sm-2 text-center">
                <div class="card-3d-wrap mx-auto">
                  <div class="card-3d-wrapper">
                    <div class="">
                      <div class="center-wrap">
                        <div class="section text-center">
                          <form>
                            <h4 style={{ color: "white" }} class="mb-4 pb-3">
                              Enter Candidate Details
                            </h4>
                            <div class="form-group">
                              <input
                                onChange={(event) => {
                                  setcname(event.target.value);
                                }}
                                type="text"
                                name="logemail"
                                class="form-style"
                                placeholder="candidate Name"
                                id="logemail"
                                autocomplete="off"
                                required
                              />
                              <i class="input-icon uil uil-at"></i>
                            </div>
                            <div class="form-group ">
                              {" "}
                              <br />
                              <input
                                onChange={saveFile}
                                type="file"
                                id="myFile"
                                name="filename"
                              />
                              <i class="input-icon uil uil-at"></i>
                            </div>
                            <a onClick={csub} class="btn mt-4">
                              Submit
                            </a>
                            :
                            <p class="mb-0 mt-4 text-center">
                              <a href="" class="link">
                                Forgot your password?
                              </a>
                            </p>
                          </form>
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}









function AcandidatecardH(props) {
  const [acand, setacand] = useState([]);
  const [cname, setcname] = useState("");
  const [cimg, setcimg] = useState("");
  const [list, setlist] = useState([]);
  const [err, seterr] = useState();
  const [count, setcount] = useState("");
  const [raw, setraw] = useState([]);
  let m = useNavigate();

  const [listofposition, setlistofposition] = useState([]);

  let { id } = useParams();

  useEffect(() => {
    axios.get(`http://localhost:8000/reg/cand/${id}`).then((res) => {
      setacand(res.data);

      console.log(res.data);
    });

    axios.post(`http://localhost:8000/reg/posone/${id}`).then((res) => {
      setlistofposition(res.data);
      console.log(res.data);
    });

    axios.get(`http://localhost:8000/reg/Result/${id}`).then((res) => {
      setraw(res.data.row);
      setlist(res.data.count);
      if (res.data.counte != 0) {
        if (list.length > 1) {
          if (res.data.count[0].count === res.data.count[1].count) {
            var t = res.data.count[0].count;
            var i = 0;
            while (res.data.count[i].count === t) {
              i++;
            }
            setcount(i);
          } else {
            setcount(1);
          }
        } else {
          setcount(1);
        }
      } else {
        setcount(0);
      }
    });
  }, []);

  let pass = "/pro";

  return (
    <div>
      <div>
        <div>
          <h1 style={{ textAlign: "center" }} className="heading">
            Candidates for {localStorage.getItem("des")}
          </h1>
          {listofposition.map((value, key) => {
            return (
              <div style={{ color: "black" }}>
                {" "}
                <Time
                  Datee={value.Date}
                  month={value.Month}
                  nav={pass}
                  year={value.Year}
                  iid={value.id}
                  hours={value.Hours}
                  minutes={value.Minutes}
                  seconds={value.Seconds}
                />
              </div>
            );
          })}
        </div>

          <div style={{ paddingRight: "10px;" }} class="container">
          <div class="row">
            {acand.map((value, key) => {
              return (
                <div class="col-sm-12 col-md-4">
                  <div style={{ textAlign: "center", color: "black" }}>
                    {value.name}
                  </div>
                  <div style={{ textAlign: "center" }}>
                    <img
                      style={{
                        width: "150px",
                        height: "150px",
                        borderRadius: "50%",
                      }}
                      src="data:image/jpeg;base64,"
                      alt="Avatar"
                      class="avatar"
                    ></img>
                  </div>
                  <div style={{ textAlign: "center" }}>
                     {/* <a
                      onClick={() => {
                        m(`/Candres/}`);
                      }}
                      class="btn btn-outline-dark off"
                    >
                      View
                    </a>  */}
                     <a
                      onClick={() => {
                        axios
                          .post(
                            `http://localhost:8000/reg/candelete/${value.id}`
                          )
                          .then((res) => {
                            window.location.reload();
                          });
                      }}
                      className="btn btn-outline-dark off"
                    >
                      Remove
                    </a>
                  </div>
                </div>
              );
            })}
          </div>
        </div>   

       
  <div style={{textAlign:'center',marginTop:'10px'}}><button className="btn btn-outline-dark off" onClick={()=>{
    m(`/winner/${id}`);
  }}>View the leaderboad</button></div>
   
      </div>
    </div>
  );
}







export default Admin;
export { Adminmain };
export { Acandidatecard };
export { Admincand };
export {AdmincandH};
export {AcandidatecardH};
