import React from "react";
import axios from "axios";
import { useEffect } from "react";
import { useState } from "react";
import Main from "./main";
import './sample.css'
import { useNavigate } from "react-router";
import web from '../mac.jpg'
import { Avatar } from "./sample";
import { useParams } from "react-router";
import './mac.jpg'

function Admin(props){


  return(
    <div>
       <Main ad='Addcontest'  home='/pro' votehere='/pro' page='Admin' card={<Adminmain />}  />
    </div>
  );
}

function Admincand(props){


  return(
    <div>
       <Main ad='Addcontest'  home='/pro' votehere='/pro' page='Result' admin={<Canform />} card={<Acandidatecard />}  />
    </div>
  );
}



function Adminmain(props){

 let l=useNavigate()
  

  const [listofpositiona, setlistofpositiona] = useState( [] );
  

  useEffect(()=>{
    axios.get("http://localhost:8000/reg/position").then((res) => {
      setlistofpositiona(res.data);
      console.log(res.data);
      
  });
},[]);
  
    return(
      <div>
         <div>
    {listofpositiona.map((value, key)=>{
      return <div style={{paddingRight:'10px' }} class="w3-col l4 m6 w3-margin-bottom">
      <div class="w3-card">
          <img src={web} alt="John" style={{width:"100%"}} />
          <div class="w3-container">
              <h4>{value.description}</h4>
              <p class="w3-opacity"></p>
              <p ><button onClick={()=>{
                l(`/Avote/${value.id}`)
      
                  localStorage.setItem('des', value.description)
             
               } } class="w3-button w3-light-grey w3-block"> View</button></p>
               <p><button onClick={()=>{

axios.post(`http://localhost:8000/reg/posdelete/${value.id}`).then((res) => {

  window.location.reload();
 

  
  
});


               }

               } class='w3-button w3-light-grey w3-block'>Delete</button></p>
          </div>
      </div>
  </div>
    })}
  </div>
  );



  
      </div>
    );
  
  
  }


  function Acandidatecard(props){

    const [acand, setacand] = useState([]);
    const [cname, setcname] = useState('');
    const [cimg, setcimg] = useState('');


  
  
  let m=useNavigate();
  
  
    let {id} =useParams();

    useEffect(()=>{
      axios.get(`http://localhost:8000/reg/cand/${id}`).then((res) => {
      setacand(res.data);
      
        console.log(res.data);
        
    });
   
  
  
  },[]);


    
	



 


  



  

    return(
      <div>
      <div>
         <div>
      <h1 style={{textAlign:'center'}} className="heading">Candidates for {localStorage.getItem('des')}</h1>
      <h3 style={{color:'black'}} ></h3>
      <div>
      <div>
    {acand.map((value, key)=>{
      
      return  <div style={{paddingRight:'10px;'}} className="card w3-col l4 m6  w3-margin-bottom">
      <div className="top">
        <h2  className="name">{value.name}</h2>
        <Avatar img={value.photo} />
      </div>
      <div className="bottom">
   <a onClick={()=>{
  
m(`/Candres/${value.id}`);


  
    }

    } class="btn btn-outline-dark off">View  </a> <a onClick={()=>{

      
axios.post(`http://localhost:8000/reg/candelete/${value.id}`).then((res) => {

  window.location.reload();
 

  
  
});
    }

    } className="btn btn-outline-dark off" >Remove</a>
      </div>
    
    </div>
    })}
  </div>
  
     </div>
  
    </div>
      </div>

      

      </div>
    );
  }

function Canform(props){

  const [cname, setcname] = useState('');
  const [cimg, setcimg] = useState('');


  let {id} =useParams();

  const csub = (req, res) => {
    const data = {name:cname, photo:web,description: localStorage.getItem('des') ,PositionTId:id};
    axios.post("http://localhost:8000/reg/candidateenter", data).then((res) => {
  if(res.data.message==="SUCCESS"){
  
    window.location.reload();
    
  }
  else{
  
  }


    });
};


  return(
    <div>
      
    <div class="section">
		<div class="container">
			<div class="row full-height justify-content-center">
				<div class="col-12 text-center align-self-center py-5">
					<div class="section pb-5 pt-5 pt-sm-2 text-center">

						<div class="card-3d-wrap mx-auto">
							<div class="card-3d-wrapper">
								<div class='card-front'>
									<div class="center-wrap">
										<div class="section text-center">
                                            <form >
											<h4 style={{color:'white'}} class="mb-4 pb-3">Enter Candidate Details</h4>
											<div class="form-group">
												<input onChange={(event)=>{setcname(event.target.value)}} type="text" name="logemail" class="form-style" placeholder="candidate Name" id="logemail" autocomplete="off" required/>
												<i class="input-icon uil uil-at"></i>
											</div>	
                      <div class="form-group "> 	<br />
                      <input onChange={(event)=>{setcimg(event.target.files[0].name)}} type="file" id="myFile" name="filename" />
												<i class="input-icon uil uil-at"></i>
											</div>	
											
                    
                                            
                                    
										<a onClick={csub} class="btn mt-4">Submit</a>:
                                        
											
                            				<p class="mb-0 mt-4 text-center"><a href="" class="link">Forgot your password?</a></p>
                                            </form>
				      					</div>
			      					</div>
			      				</div>
                  </div>
                </div>
               </div>
             </div>
           </div>
         </div>
      

        </div>
    </div>
  );
}


  export default Admin;
  export {Adminmain}
export {Acandidatecard}
export{Admincand}
