import React from "react";
import { useState } from "react";
import authentication from './firebase'
import { getAuth, RecaptchaVerifier } from "firebase/auth";


const Otptest=()=>{

 
  

    const generatecaptcha=()=>{
        window.recaptchaVerifier = new RecaptchaVerifier('recaptcha-verifier', {
            'size': 'normal',
            'callback': (response) => {
              // reCAPTCHA solved, allow signInWithPhoneNumber.
              requestotp()
            }
          }, authentication);
    
    }
    
      const requestotp=(e)=>{
        e.preventDefault()
          generatecaptcha();
          let phone="+917981759905";
          let appverifier=window.recaptchaVerifier;
          signInWithPhoneNumber(authentication,phone,appverifier).then(confirmationResult=>{
              window.confirmationResult= confirmationResult;
     
          }).catch((error) => {
          // Error; SMS not sent
          // ...
          console.log(error)
        });
        
      }

    return(
        <div style={{textAlign:'center'}}>
            <form>
              <div id='recaptcha-verifier'>
            <input placeholder='phone number' />
            <button  onClick={requestotp} type="submit">submit</button>
            </div>
            </form>

        </div>
    );
}

export default Otptest;
