import React from "react";
import './sample.css';


function Sample(){
    return(
        <div>
     


  < nav class="w3-sidebar w3-bar-block w3-small w3-hide-small  w3-center ok" >

<img src="/w3images/avatar_smoke.jpg" style={{width:"100%"}} />
<a onClick={closetoggle} class="w3-bar-item w3-button w3-padding-large w3-black">
  <i class="fa fa-home w3-xxlarge"></i>
  <p>Close x</p>
</a>
<a href="#about" class="w3-bar-item w3-button w3-padding-large w3-hover-black">
  <i class="fa fa-user w3-xxlarge"></i>
  <p>ABOUT</p>
</a>
<a href="#photos" class="w3-bar-item w3-button w3-padding-large w3-hover-black">
  <i class="fa fa-eye w3-xxlarge"></i>
  <p>PHOTOS</p>
</a>
<a href=""  class="w3-bar-item w3-button w3-padding-large w3-hover-black">
  <i class="fa fa-envelope w3-xxlarge"></i>
  <p>Logout</p>
</a>
</nav>


<nav className="w3-sidebar w3-bar-block w3-black w3-card w3-animate-left w3-hide-medium w3-hide-large"   id='mainsidebar' >
  <a href="javascript:void(0)" onClick={props.close}  class="w3-bar-item w3-button w3-large w3-padding-16"></a>
  <a href="#about"  class="w3-bar-item w3-button">Home</a>
  <a href="#team"   class="w3-bar-item w3-button">Team</a>
  <a href="#work"  class="w3-bar-item w3-button">Work</a>
  <a   class="w3-bar-item w3-button">k</a>
  <a href="#contact" onClick={closetoggle} class="w3-bar-item w3-button">close</a>
</nav>




<div class="w3-top">
  <div className="w3-bar w3-white w3-card" id="myNavbar">
    <a href="#home" className="w3-bar-item w3-button w3-wide">VOTE</a>
    <div class="w3-right ">
    <a onClick={Logout} class="w3-bar-item w3-button">Logout</a>
    </div>
    <div>
  
    </div>

    <a href="javascript:void(0)" class="w3-bar-item w3-button w3-left w3-hide-large w3-hide-medium" onClick={toggle} >
      <i class="fa fa-bars"></i>
    </a>
  </div>
</div>

        </div>
    );
}

function Avatar(props) {
  return <img className="circle-img" src={props.img} alt="avatar_img" />;
}

function Detail(props) {
  return <p className="info">{props.detailInfo}</p>;
}

function Card(props) {
  return (
    <div className="card">
      <div className="top">
        <h2 className="name">{props.name}</h2>
        <Avatar img={props.img} />
      </div>
      <div className="bottom">
        <Detail detailInfo={props.tel} />
        <Detail detailInfo={props.email} />
      </div>
    </div>
  );
}




function createCard(contact) {
  return (
    <Card
      key={contact.id}
      name={contact.name}
      img={contact.imgURL}
      tel={contact.phone}
      email={contact.email}
    />
  );
}


function E() {
  return (
    <div>
      <h1 className="heading">My Contacts</h1>
      {contacts.map(createCard)}

    </div>
  );
}


export default Sample;
