import React from "react";
import { useState } from "react";


function Homepage(){
    return(
        <div>
            <Header />
        </div>
    );
}

function Header(){
  
  function myFunction(x) {
    if (x.matches) { // If media query matches
      document.gw.style.backgroundColor = "yellow";
    } else {
     document.body.style.backgroundColor = "pink";
    }
  }
  
  var x = window.matchMedia("(max-width: 700px)")
  myFunction(x) // Call listener function at run time
  x.addListener(myFunction) // Attach listener function on state changes 

  function toggle(){
    if(document.getElementById('mySidebar'). style.display ==='block'){
      document.getElementById('mySidebar').style.display = 'none'
    }else{
      document.getElementById('mySidebar').style.display = 'block'
    }

  }

 
    return(
        <div>
            <div class="w3-top">
  <div className="w3-bar w3-white w3-card" id="myNavbar">
    <a href="#home" className="w3-bar-item w3-button w3-wide">LOGO</a>
    <div class="w3-right w3-hide-small">
      <a href="#about" class="w3-bar-item w3-button">ABOUT</a>
      <a href="#team" class="w3-bar-item w3-button"><i class="fa fa-user"></i> TEAM</a>
      <a href="#work" class="w3-bar-item w3-button"><i class="fa fa-th"></i> WORK</a>
      <a href="#pricing" class="w3-bar-item w3-button"><i class="fa fa-usd"></i> PRICING</a>
      <a href="#contact" class="w3-bar-item w3-button"><i class="fa fa-envelope"></i> CONTACT</a>
    </div>

    <a href="javascript:void(0)" class="w3-bar-item w3-button w3-right w3-hide-large w3-hide-medium"  onClick={toggle}>
      <i class="fa fa-bars"></i>
    </a>
  </div>
</div>


<nav className="w3-sidebar w3-bar-block w3-black w3-card w3-animate-left w3-hide-medium w3-hide-large"  id="mySidebar" >
  <a href="javascript:void(0)"  class="w3-bar-item w3-button w3-large w3-padding-16">Close ×</a>
  <a href="#about"  class="w3-bar-item w3-button">ABOUT</a>
  <a href="#team"   class="w3-bar-item w3-button">TEAM</a>
  <a href="#work"  class="w3-bar-item w3-button">WORK</a>
  <a href="#pricing"  class="w3-bar-item w3-button">PRICING</a>
  <a href="#contact"  class="w3-bar-item w3-button">CONTACT</a>
</nav>






        </div>
    );
}


export {Header}
export default Homepage;
