import "./style.css";
import '../node_modules/bootstrap/dist/css/bootstrap.min.css';
import React from 'react';
import {
    BrowserRouter as Router,Routes,Route
  } from "react-router-dom";

  import { useState } from "react";
import Homepage from "./components/homepage";
import Loginpage from "./components/loginpage";
import { Register } from "./components/loginpage";
import Main from "./components/main";
import axios from "axios";
import Ei from './components/sample'
import Votehere from "./components/votehere";
import { Vote } from "./components/votehere";
import Admin from "./components/admin";
import Adminloginpage from "./components/adminlogin";




export default function App() {



   const [aut, setaut] = useState(false)

   axios.get("http://localhost:8000/reg/isUserAuth", {
      headers:{"x-access-token": localStorage.getItem("token") }, }).then((res)=>{
                     if(res.data.m==='ok'){
                        setaut(localStorage.getItem('auth'))
                        setadminaut(localStorage.getItem('aut'))
                     }
                     else{}
      });
    return(
     <Router>
        <Routes>
           <Route path="/" element={<Homepage />} ></Route>
           <Route path='/login' element={<Loginpage />} ></Route>
           <Route path='/admin' element={< Adminloginpage />} ></Route>
           <Route path='/sign' element={<Register />}></Route>
           <Route path='/sample' element={<Ei />}></Route>
          <Route path="/main" element={aut ?<Main card={<Ei />} page='Home' />:<Homepage />}></Route> 
          <Route path="/votehere" element={aut ?<Votehere /> :<Homepage />}></Route>   
          <Route path="/Vote/:id" element={aut ?<Vote  />:<Homepage />}></Route> 
          <Route path="/pro/:id" element={aut?<Admin />:<Homepage /> }></Route>

        </Routes>
     </Router>
    ); }
