import {React,useRef} from "react";
import './sample.css';
import web from '../mac.jpg'
import { useNavigate } from "react-router";
import { useState } from "react";
import { useEffect } from "react";
import axios from "axios";
import { useParams } from 'react-router-dom';













function Vt() {
  const [cand, setcand] = useState([]);
  const [bt, setbt] = useState('');
  const [Active, setActive] = useState('');


  let {id} =useParams();
 
  

  useEffect(()=>{



    axios.post(`http://localhost:8000/reg/voted/${id}/${localStorage.getItem('usid')}`).then((res) => {
      if(res.data.s==="fail"){
      console.log(res);
      setbt(true);
      

      }
      else{
        setbt(false);
        setActive(res.data.cId);
      
      
        
      }
  
    });

 
      
  
  });

 


 
  





  



  useEffect(()=>{
    axios.get(`http://localhost:8000/reg/cand/${id}`).then((res) => {
    setcand(res.data);
    
      console.log(res.data);
      
  });
 


},[]);



//countdown

const [seconds, setseconds] = useState('0');
const [minute,setminute]=useState('0');
const [Hours,setHours] = useState('0');

  axios.get(`http://localhost:8000/reg/postime/${id}`).then((res) => {
    localStorage.setItem('time', res.data.Time);
    
});

let nav =useNavigate();
let interval =useRef();

    const starttimer=()=>{
  
  interval = setInterval(()=>
  {
    const ctar=localStorage.getItem('time');
  const count= new Date().getTime();
  const distance=ctar-count;
  const hours= Math.floor((distance% (1000*60*60*24)/(1000*60*60)))
  const min= Math.floor((distance% (1000*60*60))/(1000*60));
  const sec= Math.floor((distance% (1000*60))/1000);
  if(distance<0){
    axios.post(`http://localhost:8000/reg/posdelete/${id}`).then((res) => {
    nav('/main')
  window.location.reload(); 
});
  }
  else{
    setHours(hours);
    setminute(min);
    setseconds(sec);
  }
},1000)

}
 

useEffect(()=>{
  starttimer();
  return ()=> {clearInterval(interval.current)
  };
});

 


  return (
    <div>
      <h1 style={{textAlign:'center'}} className="heading">Candidates for {localStorage.getItem('des')}</h1>
      <h3 style={{color:'black'}} > Vote for only one  candidate  </h3>
      <div style={{textAlign:'center'}}>
      <div style={{display:'flex',justifyContent:'center'}}> 
        <div style={{margin:'10px',padding:'10px',fontSize:'20px',backgroundColor:'black',color:'white',borderRadius:'10px'}}>{isNaN(Hours)? '00': (Hours<10)?'0'+Hours:Hours}</div>
        <div style={{margin:'10px',padding:'10px',fontSize:'20px'}}>:</div>
        <div style={{margin:'10px',padding:'10px',fontSize:'20px',backgroundColor:'black',color:'white'}}>{isNaN(minute)?'00':(minute<10)?'0'+minute:minute}</div>
        <div style={{margin:'10px',padding:'10px',fontSize:'20px'}}>:</div>
        <div style={{margin:'10px',padding:'10px',fontSize:'20px',backgroundColor:'black',color:'white'}}>{isNaN(seconds)?'00':(seconds<10)?'0'+seconds:seconds}</div>
      </div>
      </div>
      
      <div>
      <div>
    {cand.map((value, key)=>{
      
      return  <div style={{paddingRight:'10px;'}} className="card w3-col l4 m6  w3-margin-bottom">
      <div className="top">
        <h2 id={value.id}  className="name">{value.name}</h2>
        <Avatar img={value.photo} />
      </div>
      <div className="bottom">
    {bt? <a onClick={()=>{
        localStorage.setItem('cid', value.id)

  
        const data = {  CandidateTId: localStorage.getItem('cid')  , PositionTId: id,  UseridId: localStorage.getItem('usid')};
        axios.post("http://localhost:8000/reg/Votes", data).then((res) => {
            if(res.data.ok==='success'){
              setbt(false);
              setActive(CandidateTId);
            

            }else{
              setbt(true);
        

            }

              
      
        });

      }}  class="btn btn-outline-dark off">Vote </a>: <a  >Completed</a>}
      
      </div>
    </div>
    })}
  </div>
  
     </div>
  
    </div>
  );
}




function Avatar(props) {
  return <img className="circle-img" src={props.img} alt="avatar_img" />;
}


function Card(props) {




  let l=useNavigate()

  const [listofposition, setlistofposition] = useState( [] );
  
  

  useEffect(()=>{
    axios.get("http://localhost:8000/reg/position").then((res) => {
      setlistofposition(res.data);
      console.log(res.data);
      
      
  });
},[]);




  return (
  <div>
    {listofposition.map((value, key)=>{
      return <div style={{paddingRight:'10px' }} class="w3-col l4 m6 w3-margin-bottom">
      <div class="w3-card">
          <img src={web} alt="John" style={{width:"100%"}} />
          <div class="w3-container">
              <h4>{value.description}</h4>
              <p class="w3-opacity"></p>
              <p ><button onClick={()=>{
                l(`/Vote/${value.id}`);
                  localStorage.setItem('des', value.description)
               } } class="w3-button w3-light-grey w3-block"> View</button></p>
          </div>
      </div>
  </div>
    })}
  </div>
  );
}







function Ei() {

  return (
    <div >

      <Card />
      

    </div>
  );
}

export default Ei
export {Vt}
export {Avatar}
